  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import { Link } from 'docz'

const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}>
<MDXTag name="h2" components={components} props={{"id":"factorygenerator"}}><MDXTag name="inlineCode" components={components} parentName="h2">{`factory(generator)`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`args`}</MDXTag>{`:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="inlineCode" components={components} parentName="li">{`generator (FactoryGenerator)`}</MDXTag>{`: Generator function which describes your data object. This function receives a `}<MDXTag name="inlineCode" components={components} parentName="li">{`faker`}</MDXTag>{` instance as an argument`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`return`}</MDXTag>{`:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="inlineCode" components={components} parentName="li">{`Factory object (IFactoryObject)`}</MDXTag>{`: The factory object contains four methods: `}<Link to="/api/create"><MDXTag name="inlineCode" components={components} parentName="li">{`create`}</MDXTag></Link>{`, `}<Link to="/api/only"><MDXTag name="inlineCode" components={components} parentName="li">{`only`}</MDXTag></Link>{`, `}<Link to="/api/make"><MDXTag name="inlineCode" components={components} parentName="li">{`make`}</MDXTag></Link>{` and `}<Link to="/api/seed"><MDXTag name="inlineCode" components={components} parentName="li">{`seed`}</MDXTag></Link></MDXTag>
</MDXTag>
<hr style={{ marginTop: '2rem', marginBottom: '2rem', opacity: '0.2' }} />
<MDXTag name="h3" components={components} props={{"id":"description"}}>{`Description`}</MDXTag>
<MDXTag name="p" components={components}>{`You should create a factory object for the data you want to generate. To do that, you need to specify the type of data. To do this, the
factory class makes use of the `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/marak/Faker.js/"}}>{`Faker.js`}</MDXTag>{` package which provides an easy way to generate random data.`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`import { factory } from 'node-factory';

const UserFactory = factory(fake => ({
  id: fake.random.uuid(),
  name: fake.name.findName(),
  email: fake.internet.email(),
}));

export default UserFactory;
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
